function getChordChart(name) {
  // Themes begin
  am4core.useTheme(am4themes_animated);
  // Themes end

  var chart = am4core.create("chartdiv", am4charts.ChordDiagram);

  // colors of main characters
  chart.colors.saturation = 0.45;
  chart.colors.step = 3;
  var colors = {
      //Julio:chart.colors.next(),
      //Ramon:chart.colors.next(),
      //Carlos:chart.colors.next(),
      //Juan:chart.colors.next(),
      //Jorge:chart.colors.next(),
      //Pepe:chart.colors.next()
  }
  if(name == 1) {
    var dialogues = dialoguesEpisode1;
    chart.data = dialoguesEpisode1;
  } else if(name == 2) {
    var dialogues = dialoguesEpisode2;
    chart.data = dialoguesEpisode2;
  } else if(name == 3) {
    var dialogues = dialoguesEpisode3;
    chart.data = dialoguesEpisode3;
  } else if(name == 4) {
    var dialogues = dialoguesEpisode4;
    chart.data = dialoguesEpisode4;
  } else if(name == 5) {
    var dialogues = dialoguesEpisode5;
    chart.data = dialoguesEpisode5;
  } else if(name == 6) {
    var dialogues = dialoguesEpisode6;
    chart.data = dialoguesEpisode6;
  } else if(name == 7) {
    var dialogues = dialoguesEpisode7;
    chart.data = dialoguesEpisode7;
  } else if(name == 8) {
    var dialogues = dialoguesEpisode8;
    chart.data = dialoguesEpisode8;
  } else if(name == 9) {
    var dialogues = dialoguesEpisode9;
    chart.data = dialoguesEpisode9;
  } else if(name == 10) {
    var dialogues = dialoguesEpisode10;
    chart.data = dialoguesEpisode10;
  } else if(name == 11) {
    var dialogues = dialoguesEpisode11;
    chart.data = dialoguesEpisode11;
  } else if(name == 12) {
    var dialogues = dialoguesEpisode12;
    chart.data = dialoguesEpisode12;
  } else if(name == 13) {
    var dialogues = dialoguesEpisode13;
    chart.data = dialoguesEpisode13;
  } else if(name == 14) {
    var dialogues = dialoguesEpisode14;
    chart.data = dialoguesEpisode14;
  } else if(name == 15) {
    var dialogues = dialoguesEpisode15;
    chart.data = dialoguesEpisode15;
  } else if(name == 16) {
    var dialogues = dialoguesEpisode16;
    chart.data = dialoguesEpisode16;
  } else if(name == 17) {
    var dialogues = dialoguesEpisode17;
    chart.data = dialoguesEpisode17;
  } else if(name == 18) {
    var dialogues = dialoguesEpisode18;
    chart.data = dialoguesEpisode18;
  } else if(name == 19) {
    var dialogues = dialoguesEpisode19;
    chart.data = dialoguesEpisode19;
  } else if(name == 20) {
    var dialogues = dialoguesEpisode20;
    chart.data = dialoguesEpisode20;
  } else if(name == 21) {
    var dialogues = dialoguesEpisode21;
    chart.data = dialoguesEpisode21;
  } else if(name == 22) {
    var dialogues = dialoguesEpisode22;
    chart.data = dialoguesEpisode22;
  }

  chart.dataFields.fromName = "from";
  chart.dataFields.toName = "to";
  chart.dataFields.value = "value";


  chart.nodePadding = 0.5;
  chart.minNodeSize = 0.01;
  chart.startAngle = 80;
  chart.endAngle = chart.startAngle + 360;
  chart.sortBy = "value";
  chart.fontSize = 10;

  var nodeTemplate = chart.nodes.template;
  nodeTemplate.readerTitle = "Click to show/hide or drag to rearrange";
  nodeTemplate.showSystemTooltip = true;
  nodeTemplate.propertyFields.fill = "color";
  nodeTemplate.tooltipText = "{name}'s dialogs: {total}";

  // when rolled over the node, make all the links rolled-over
  nodeTemplate.events.on("over", function(event) {
      var node = event.target;

      node.outgoingDataItems.each(function(dataItem) {
          if(dataItem.toNode){
              dataItem.link.isHover = true;
              dataItem.toNode.label.isHover = true;
          }
      })
      node.incomingDataItems.each(function(dataItem) {
          if(dataItem.fromNode){
              dataItem.link.isHover = true;
              dataItem.fromNode.label.isHover = true;
          }
      })

      node.label.isHover = true;
  })

  // when rolled out from the node, make all the links rolled-out
  nodeTemplate.events.on("out", function(event) {
      var node = event.target;

      node.outgoingDataItems.each(function(dataItem) {
          if(dataItem.toNode){
              dataItem.link.isHover = false;
              dataItem.toNode.label.isHover = false;
          }
      })
      node.incomingDataItems.each(function(dataItem) {
          if(dataItem.fromNode){
              dataItem.link.isHover = false;
             dataItem.fromNode.label.isHover = false;
          }
      })

      node.label.isHover = false;
  })

  var label = nodeTemplate.label;
  label.relativeRotation = 90;

  label.fillOpacity = 0.4;
  let labelHS = label.states.create("hover");
  labelHS.properties.fillOpacity = 1;

  nodeTemplate.cursorOverStyle = am4core.MouseCursorStyle.pointer;
  // this adapter makes non-main character nodes to be filled with color of the main character which he/she kissed most
  nodeTemplate.adapter.add("fill", function(fill, target) {
      let node = target;
      let counters = {};
      let mainChar = false;
      node.incomingDataItems.each(function(dataItem) {
          if(colors[dataItem.toName]){
              mainChar = true;
          }

          if(isNaN(counters[dataItem.fromName])){
              counters[dataItem.fromName] = dataItem.value;
          }
          else{
              counters[dataItem.fromName] += dataItem.value;
          }
      })
      if(mainChar){
          return fill;
      }

      let count = 0;
      let color;
      let biggest = 0;
      let biggestName;

      for(var name in counters){
          if(counters[name] > biggest){
              biggestName = name;
              biggest = counters[name];
          }
      }
      if(colors[biggestName]){
          fill = colors[biggestName];
      }

      return fill;
  })

  var links = chart.links;

  // link template
  var linkTemplate = chart.links.template;
  linkTemplate.strokeOpacity = 0;
  linkTemplate.fillOpacity = 0.15;
  linkTemplate.tooltipText = "{fromName} & {toName}:{value.value}";

  linkTemplate.events.on("over", function(event) {
  	var link = event.target;
  	var pair = link.dataItem.fromName + "-" + link.dataItem.toName;
  	for(var i in dialogues) {
  		if(dialogues[i].pair == pair) {
        if(dialogues[i].from == 'Barney') {
          $("#from").attr("src", "Barney.jpeg");
        } else if(dialogues[i].from == 'Ted') {
          $("#from").attr("src", "Ted.jpeg");
        } else if(dialogues[i].from == 'Marshall') {
          $("#from").attr("src", "Marshall.png");
        } else if(dialogues[i].from == 'Lily') {
          $("#from").attr("src", "Lily.png");
        } else if(dialogues[i].from == 'Robin') {
          $("#from").attr("src", "Robin.png");
        } else {
          $("#from").attr("src", "secondaryCharacter.png");
        }
        if(dialogues[i].to == 'Barney') {
          $("#to").attr("src", "Barney.jpeg");
        } else if(dialogues[i].to == 'Ted') {
          $("#to").attr("src", "Ted.jpeg");
        } else if(dialogues[i].to == 'Marshall') {
          $("#to").attr("src", "Marshall.png");
        } else if(dialogues[i].to == 'Lily') {
          $("#to").attr("src", "Lily.png");
        } else if(dialogues[i].to == 'Robin') {
          $("#to").attr("src", "Robin.png");
        } else {
          $("#to").attr("src", "secondaryCharacter.png");
        }

  			$("#dialogs").empty();
  			for(var d in dialogues[i].texts) {
  				var li  = document.createElement('li'),
    				txt = document.createTextNode(dialogues[i].texts[d]);

  				li.appendChild(txt);
  				$("#dialogs").append(li);
  				$("#dialogs li").addClass('list-group-item');
  			}
  		}
  	}
  });


  var hoverState = linkTemplate.states.create("hover");
  hoverState.properties.fillOpacity = 0.7;
  hoverState.properties.strokeOpacity = 0.7;


  var titleImage = chart.chartContainer.createChild(am4core.Image);
  titleImage.href = "himym-dialogs.png";
  titleImage.x = 30
  titleImage.y = 30;
  titleImage.width = 185;
  titleImage.height = 185;
}
